n        descripTd.className = \"tdout\";\n        var span = $.createElement(\"span\", \"spanout\");\n        descripTd.appendChild(span);\n        \n        var imgNum = 0;\n        function changeImg(){ \n            if (imgNum < picUrl.length - 1) { imgNum++; } \n	    else { imgNum = 0; }\n\n            img.src = picUrl[imgNum];   // 显示图片\n            span.title = title[imgNum]; // 显示标题\n            \n	    var smallTitle = title[imgNum];\n            span.innerHTML = smallTitle.length > 15 ? smallTitle.substring(0,15) + \"...\" : smallTitle;\n\n            span.onmouseover = function() { descripTd.className = \"tdover\"; }\n            span.onmouseout = function() { descripTd.className = \"tdout\"; }\n\n			// 显示描述\n            if(content[imgNum].length > 100){\n                contentSpan.innerHTML = content[imgNum].substring(0,100) + \"...\";\n            } else {\n                contentSpan.innerHTML = content[imgNum];\n            }\n            \n            var thisUrl = \"${common.articlePageUrl}articleId=\" + articleIds[imgNum];\n            img.onclick = span.onclick = contentSpan.onclick = function(){\n                window.open(thisUrl);\n            }\n\n            setTimeout(changeImg, 3000);\n        }\n\n        changeImg();\n    }\n}]]>\n</script>\n<style>\n<![CDATA[#${id} .tdover{  cursor:pointer; }\n#${id} .tdout{ }\n#${id}_descrip{ font-size:12px; text-align:center; }\n#${id}_pic { visibility:hidden; width:212px; height:156px; cursor:pointer; }\n#${id}_content { vertical-align:top; padding-top:10px;word-break:break-all; cursor:pointer; text-indent:22px; }]]>\n</style>\n<html>\n<![CDATA[<div class=\"portlet\" id=\"${id}\">\n	<table width=\"250\">\n		<tr>\n			<td style=\"padding-top:5px;padding-left:3px\">\n				<img src=\"${basepath}loading.gif\" id=\"${id}_pic\"/>\n			</td>\n		</tr>\n		<tr> <td id=\"${id}_descrip\"></td> </tr>\n		<tr> <td id=\"${id}_content\"></td> </tr>\n	</table>\n</div>]]>\n</html>\n<events>\n<attach event=\"onload\" oneve