;\n		});\n	}\n	request.send();\n}\n\nArticleList.setArticleList = function(curNode){\n    var thisObj = this;\n\n    // 获取配置参数\n    var columns = [\"title\", \"issueDate\"];\n    if( this.columns ){\n        columns = this.columns.split(\",\");\n    }\n\n	// 左侧大标题\n	var channelName = $(\"channelName\", curNode).text();\n	if( channelName ){\n		$(\"#Left_big_title\").html( channelName );\n	}\n\n    var currentPage = parseInt(thisObj.page);\n	var pageSize    = parseInt(thisObj.pageSize);\n	var totalPage = $(\"totalPageNum\", curNode).text();\n    var articleNodes = $(\"item\", curNode);\n    if( articleNodes.length ) {\n		articleNodes.each(function(i, articleNode){\n			var count = i + (currentPage - 1) * pageSize + 1;\n            thisObj.createArticleItem(articleNode, columns, count);\n		});\n    } else {\n        this.innerHTML = \"<table><tbody><tr class=\\\"item\\\"><td class=\\\"title\\\">- 该栏目还没有文章 -</td></tr></tbody></table>\";\n    }\n\n	if(this.needPageInfo == \"true\"){\n		var currentPage = parseInt(thisObj.page);\n		if ( totalPage && totalPage >= 1){\n			thisObj.setPage(currentPage, totalPage);\n		}\n	}\n	else {\n		$(\"thead\", thisObj).hide();\n		$(\".indexTd\", thisObj).hide();\n	}\n}\n\n/*\n *	函数说明：创建列表项\n *	参数：  XmlNode:articleNode        XmlNode实例\n            Array:columns           显示列顺序的数组\n */\nArticleList.createArticleItem = function(articleNode, columns, count){\n    var thisObj = this;\n\n    var tableObj = $(\"tbody\", this)[0];\n    if( !tableObj ) {\n		this.innerHTML = \"<table class=\'articleTable\'><thead><tr><td>编号</td><td>标题</td><td>出处</td><td>时间</td></tr></thead><tbody></tbody></table>\";\n        tableObj = $(\"tbody\", this)[0];\n    }\n\n    var tr = $.createElement(\"tr\");\n	tableObj.appendChild(tr);\n\n	// 编号\n	var tdBH = $.createElement(\"td\", \"indexTd\");\n	$(tdBH).html(count);\n	tr.appendChild(tdBH);\n\n	columns.each(function(i, column){\n		var tdXX = $.createElement(\"td\", column);\n        tdXX.innerHTML = tdXX.title = $(col